      if (i != j) {
        # Calculate conditional probability q_j|i using the same Gaussian kernel
        Q[i, j] &lt;- exp(-(Dy[i, j])^2)

      }
    }
  }

  Q &lt;- Q / sum(Q)
  grad &lt;- matrix(0, nrow = nrow(D), ncol = ndim)
  # For each point i and j, compute the gradient of KL divergence
